-- Using CTE to generate numbers 1 and 2 for each row in table2\nWITH NumberSequence AS (\n    SELECT 1 AS num UNION ALL\n    SELECT 2\n)\nINSERT INTO Table1 (column1, column2, column3, column4, column5, foreignKey)\nSELECT \n    'a' AS column1,\n    'a' AS column2,\n    'a' AS column3,\n    'a' AS column4,\n    'a' AS column5,\n    t2.id AS foreignKey\nFROM Table2 t2\nCROSS JOIN NumberSequence;