-- Define the percentage to update\nDECLARE @Percentage FLOAT = 0.07;\n\n-- Use a Common Table Expression (CTE) for clarity and performance\nWITH RankedContacts AS (\n    SELECT\n        *,\n        ROW_NUMBER() OVER (PARTITION BY ContactAssociate ORDER BY NEWID()) as RowNum,\n        COUNT(*) OVER (PARTITION BY ContactAssociate) as TotalCount\n    FROM tb_Contact\n)\nUPDATE RankedContacts\nSET ContactAssociate = 'Peter'\nWHERE RowNum <= CAST(TotalCount * @Percentage AS INT);