-- Using separate ID fields for flexibility\nCREATE TABLE Foo (\n    fooID INT PRIMARY KEY AUTO_INCREMENT,\n    -- other columns\n);\n\nCREATE TABLE Tag (\n    tag VARCHAR(50) PRIMARY KEY\n);\n\nCREATE TABLE FooTag (\n    FooTagID INT PRIMARY KEY AUTO_INCREMENT,  -- unique ID for each record\n    fooID INT,\n    tag VARCHAR(50),\n    PRIMARY KEY (FooTagID), \n    FOREIGN KEY (fooID) REFERENCES Foo(fooID),\n    FOREIGN KEY (tag) REFERENCES Tag(tag)\n);\n\n-- Using composite key directly\nCREATE TABLE FooTag (\n    fooID INT,\n    tag VARCHAR(50),\n    PRIMARY KEY (fooID, tag),  -- combined primary key\n    FOREIGN KEY (fooID) REFERENCES Foo(fooID)\n);