-- Assuming there is an Organizations table\nCREATE TABLE Organizations (\n    OrganizationID INT PRIMARY KEY,\n    Name VARCHAR(255),\n    -- Other organization fields...\n);\n\n-- And a Sample Users table before adding OrganizationID\nCREATE TABLE Users (\n    UserID INT PRIMARY KEY,\n    Username VARCHAR(255)\n    -- Other user fields...\n);\n\n-- Modify the Users table to include OrganizationID\nALTER TABLE Users \nADD COLUMN OrganizationID INT NOT NULL;\n\n-- Add a foreign key constraint\nALTER TABLE Users \nADD CONSTRAINT fk_organization_user \nFOREIGN KEY (OrganizationID) REFERENCES Organizations(OrganizationID);