-- Table to store users and their hashed passwords\nCREATE TABLE users (\n    username VARCHAR(100) NOT NULL PRIMARY KEY,\n    password CHAR(64) NOT NULL,  -- Assuming use of SHA-256 hash\n    salt CHAR(36) DEFAULT NULL   -- For unique salts per user\n);\n\n-- Table to store roles for users\nCREATE TABLE user_roles (\n    username VARCHAR(100) NOT NULL,\n    role_name VARCHAR(100) NOT NULL,\n    PRIMARY KEY (username, role_name),\n    FOREIGN KEY (username) REFERENCES users(username)\n);\n\n-- Table to store permissions associated with roles\nCREATE TABLE roles_permissions (\n    role_name VARCHAR(100) NOT NULL,\n    permission VARCHAR(255) NOT NULL,\n    PRIMARY KEY (role_name, permission),\n    FOREIGN KEY (role_name) REFERENCES user_roles(role_name)\n);