SELECT \n    m.id AS media_id,\n    m.uri AS media_uri,\n    GROUP_CONCAT(CASE WHEN p.role = 1 THEN p.name END, ', ') AS person_actor,\n    GROUP_CONCAT(CASE WHEN p.role = 2 THEN p.name END, ', ') AS person_artist,\n    GROUP_CONCAT(CASE WHEN p.role = 3 THEN p.name END, ', ') AS person_publisher\nFROM \n    media m\nJOIN \n    media_to_people mtp ON m.id = mtp.media_id\nJOIN \n    people p ON mtp.people_id = p.id\nGROUP BY \n    m.id, m.uri;