-- Publisher: Configure distribution database and publication\nUSE master;\nEXEC sp_replicationdboption 'YourDatabaseName', 'publish', 1;\n\n-- Create a snapshot agent job\nDECLARE @publication as sysname;\nSET @publication = N'YourPublicationName';\n\n-- Adding the transactional publication.\nUSE [YourDatabaseName]\nEXEC sp_addpublication\n    @publication = @publication,\n    @description = N'Transactional publication of membership tables',\n    @sync_type = N'autosync',\n    @retention = 72;\nGO\n\n-- Create an article for each table, e.g., aspnet_Users\nUSE [YourDatabaseName]\nEXEC sp_addarticle \n    @publication = @publication,\n    @article = N'aspnet_Users',\n    @source_owner = N'dbo',\n    @source_object = N'aspnet_Users';\nGO\n\n-- Configure subscriber(s)\nUSE [distribution];\nEXEC sp_addsubscription\n    @publication = @publication,\n    @subscriber = N'SubscriberServerName',\n    @destination_db = N'SubscriberDatabaseName',\n    @sync_type = N'autosync';\n\nEXEC sp_addpushsubscription_agent\n    @publication = @publication,\n    @subscriber = N'SubscriberServerName',\n    @subscriber_db = N'SubscriberDatabaseName',\n    @job_login = NULL,\n    @job_password = NULL;\nGO