-- For first query\nSELECT @AloneRecordCount = count(*) \nFROM inserted i \nINNER JOIN deleted d on i.id = d.id \nWHERE i.StatusID = 32 \nAND d.StatusID <> 32 \nAND i.id IN (SELECT settlementid FROM vwFundingDisbursement fd WHERE fd.DisbTypeName LIKE '%Alone' AND fd.PaymentMethodID = 0);\n\n-- For second query\nSELECT @AloneRecordCount = count(i.id) \nFROM inserted i \nINNER JOIN deleted d on i.id = d.id\nJOIN vwFundingDisbursement fd on i.id = fd.settlementid\nWHERE i.StatusID = 32 \nAND d.StatusID <> 32  \nAND fd.DisbTypeName LIKE '%Alone'\nAND fd.PaymentMethodID = 0;