WITH CombinedTables AS (\n    SELECT Column1 AS c1, ..., ColumnN AS cN, 'database1' as source_db, 'Table1' as source_table FROM database1.dbo.Table1\n    UNION ALL\n    SELECT 'Some String' as c1, ..., NULL as cN, 'database1' as source_db, 'Table2' as source_table FROM database1.dbo.Table2\n    UNION ALL\n    SELECT Column1 AS c1, ..., ColumnN AS cN, 'database2' as source_db, 'Table1' as source_table FROM database2.dbo.Table1\n    UNION ALL\n    SELECT 'Some String' as c1, ..., NULL as cN, 'database2' as source_db, 'Table2' as source_table FROM database2.dbo.Table2\n)\nSELECT DISTINCT c1, ..., cN\nFROM CombinedTables;