WITH RECURSIVE DateRange AS (\n    SELECT DATE('2009-01-30') as day\n    UNION ALL\n    SELECT DATE_ADD(day, INTERVAL 1 DAY) \n    FROM DateRange \n    WHERE DATE_ADD(day, INTERVAL 1 DAY) <= '2009-03-01' -- end date here\n)\nSELECT dr.day, COALESCE(SUM(mt.important_data), 0) as important_data\nFROM DateRange dr\nLEFT JOIN my_table mt ON DATE_FROM_UNIXTIME(mt.date) = dr.day\nWHERE dr.day BETWEEN '2009-01-30' AND '2009-03-01' -- your date range here\nGROUP BY dr.day\nORDER BY dr.day;