SELECT [dbo].tableB.theColumnINeed \nFROM [dbo].tableA \nLEFT OUTER JOIN [dbo].tableB \nON [dbo].tableA.myColumn = FormatColumnOneWay([dbo].tableB.myColumn)\nWHERE [dbo].tableA.myDateColumn > '1/1/2009'\n\nUNION ALL\n\nSELECT [dbo].tableB.theColumnINeed \nFROM [dbo].tableA \nLEFT OUTER JOIN [dbo].tableB \nON [dbo].tableA.myColumn = FormatColumnAnotherWay([dbo].tableB.myColumn)\nWHERE [dbo].tableA.myDateColumn <= '1/1/2009'