-- Create a publication that uses a minimal snapshot\nEXEC sp_addpublication \n    @publication = N'MyPublication', \n    @description = N'Transactional replication publication of the database.', \n    @sync_type = N'initialize with backup', \n    @repl_freq = N'continuous', \n    @status = N'active', \n    @allow_push = N'true', \n    @allow_pull = N'true', \n    @immediate_sync = N'minimal';