SELECT \n    ps.player_id,\n    p.name,\n    p.surname,\n    COALESCE(t.count_tries, 0) AS triesNo\nFROM \n    players ps\nJOIN \n    player p ON p.player_id = ps.player_id\nLEFT JOIN \n    (SELECT \n         player_id, \n         COUNT(*) AS count_tries \n     FROM \n         tries \n     WHERE \n         game_id = '$game_id' \n     GROUP BY \n         player_id) t \nON \n    t.player_id = ps.player_id\nWHERE \n    ps.game_id = '$game_id'\nORDER BY \n    ps.number;