-- Publisher Setup\nUSE master;\nGO\n\nEXEC sp_replicationdboption \n@dbname = N'YourMainDatabase',\n@property = 'publish',\n@value = N'true';\nGO\n\n-- Distributor Setup\nUSE master;\nGO\n\nEXEC sp_adddistributor \n@distributor = N'ServerName';\n\nEXEC sp_adddistributiondb \n@database = N'distribution';\nGO\n\n-- Publication Creation\nUSE YourMainDatabase;\nGO\n\nEXEC sp_addpublication \n@publication = N'YourPublication',\n@description = N'Transactional publication of database.',\n@sync_method = N'native',\n@retention = 365, -- days to retain transactions\n@allow_push = N'true';\n\n-- Article Addition\nEXEC sp_addarticle \n@publication = N'YourPublication', \n@article = 'dbo.YourTable', \n@source_owner = 'dbo', \n@source_object = 'YourTable', \n@type = N'logbased',\n@description = N'Article for replication.',\n@sync_type = N'automatic';\n\n-- Subscription Setup on Subscriber\nUSE distribution;\nGO\n\nEXEC sp_addsubscription \n@publication = N'YourPublication', \n@subscriber = N'SubscriberServerName', \n@destination_db = N'DestinationDB',\n@subscription_type = N'Push',\n@sync_type = 'automatic',\n@update_mode = N'read only';\n\nEXEC sp_addpushsubscription_agent \n@publisher = N'MainServerName', \n@publication = N'YourPublication', \n@subscriber = N'SubscriberServerName', \n@job_login = null, \n@job_password = null, \n@subscriber_security_mode = 1;