CREATE TABLE #temp(\n    [count] int,\n    [Time] datetime\n)\n\nDECLARE @DateNow DATETIME, @i int\n\nSET @DateNow = '00:00'\nSET @i = 1\n\nWHILE (@i < 1440)\nBEGIN\n    SET @DateNow=DATEADD(minute, 1, @DateNow)\n    INSERT INTO #temp ([count], [Time]) VALUES (0, @DateNow)\n    SET @i=@i+1\nEND\n\nSELECT \n    [count],\n    CONVERT(VARCHAR(5), [Time], 108) AS [Time] -- Using style 108 to get HH:mm format\nFROM  \n    #temp\n\nDROP TABLE #temp