-- Insert into users table\nINSERT INTO users (id, firstName, surname)\nSELECT id, firstName, surname\nFROM customers;\n\n-- Insert billing addresses into the addresses table\nINSERT INTO addresses (id, user_id, street1, street2, town, county, postcode, country, type)\nSELECT \n    id * 2 - 1 AS address_id, -- Assuming unique ids for each record; adjust as necessary\n    id AS user_id,\n    address1 AS street1,\n    address2 AS street2,\n    town,\n    county,\n    postcode,\n    country,\n    'billing' AS type\nFROM customers;\n\n-- Insert delivery addresses into the addresses table\nINSERT INTO addresses (id, user_id, street1, street2, town, county, postcode, country, type)\nSELECT \n    id * 2 AS address_id, -- Continuing unique ids for delivery addresses\n    id AS user_id,\n    delAddress1 AS street1,\n    delAddress2 AS street2,\n    delTown AS town,\n    delCounty AS county,\n    delPostcode AS postcode,\n    delCountry AS country,\n    'delivery' AS type\nFROM customers;\n\n-- Insert home phone numbers into the phone_numbers table\nINSERT INTO phone_numbers (id, user_id, number, type)\nSELECT \n    id * 3 - 2 AS phone_id, -- Adjust to maintain unique ids \n    id AS user_id,\n    tel AS number,\n    'home' AS type\nFROM customers;\n\n-- Insert mobile phone numbers into the phone_numbers table if they exist\nINSERT INTO phone_numbers (id, user_id, number, type)\nSELECT \n    id * 3 - 1 AS phone_id, -- Adjust to maintain unique ids \n    id AS user_id,\n    mobile AS number,\n    'mobile' AS type\nFROM customers\nWHERE mobile IS NOT NULL AND mobile != '';\n\n-- Insert work phone numbers into the phone_numbers table if they exist\nINSERT INTO phone_numbers (id, user_id, number, type)\nSELECT \n    id * 3 AS phone_id, -- Adjust to maintain unique ids \n    id AS user_id,\n    workTel AS number,\n    'work' AS type\nFROM customers\nWHERE workTel IS NOT NULL AND workTel != '';