SELECT \n    COALESCE(vd.date, td.date) AS Date,\n    SUM(COALESCE(vd.STT, 0)) AS STT,\n    SUM(COALESCE(td.VA, 0)) AS VA\nFROM \n    (SELECT tblAllDates.date, SUM(tblvolumedata.STT) AS STT\n     FROM tblvolumedata RIGHT JOIN tblAllDates ON tblvolumedata.Date = tblAllDates.Date\n     GROUP BY tblAllDates.Date) vd\nFULL OUTER JOIN \n    (SELECT tblAllDates.date, \n            SUM(NZ(tblTimesheetData.batching) + NZ(tblTimesheetData.categorisation) + NZ(tblTimesheetData.CDT) +\n                NZ(tblTimesheetData.CSI) + NZ(tblTimesheetData.destruction) + NZ(tblTimesheetData.extraction) +\n                NZ(tblTimesheetData.indexing) + NZ(tblTimesheetData.mail) + NZ(tblTimesheetData.newlodgement) +\n                NZ(tblTimesheetData.recordedDeliveries) + NZ(tblTimesheetData.retrieval) + NZ(tblTimesheetData.scanning)) AS VA\n     FROM tblTimesheetData RIGHT JOIN tblAllDates ON tblTimesheetData.Date = tblAllDates.Date\n     GROUP BY tblAllDates.Date) td\nON vd.date = td.date\nGROUP BY COALESCE(vd.date, td.date)\nORDER BY Date;