-- Replace 'old_ip' with your old server IP and 'new_ip' with your new server IP\n\nUPDATE wp_options SET option_value = replace(option_value, 'http://old_ip', 'http://new_ip') WHERE option_name = 'home' OR option_name = 'siteurl';\n\nUPDATE wp_posts SET guid = replace(guid, 'http://old_ip','http://new_ip');\n\nUPDATE wp_posts SET post_content = replace(post_content, 'http://old_ip', 'http://new_ip');\n\nUPDATE wp_postmeta SET meta_value = replace(meta_value,'http://old_ip','http://new_ip');