-- Table to store all possible dates\nCREATE TABLE dates (\n    date_id INT PRIMARY KEY AUTO_INCREMENT,\n    date_name VARCHAR(255) UNIQUE NOT NULL\n);\n\n-- Table to store all possible questions that can be asked for any date\nCREATE TABLE questions (\n    question_id INT PRIMARY KEY AUTO_INCREMENT,\n    question_text TEXT NOT NULL\n);\n\n-- A bridge table linking dates and questions\nCREATE TABLE date_questions (\n    date_question_id INT PRIMARY KEY AUTO_INCREMENT,\n    date_id INT,\n    question_id INT,\n    FOREIGN KEY (date_id) REFERENCES dates(date_id),\n    FOREIGN KEY (question_id) REFERENCES questions(question_id)\n);\n\n-- Table to store user answers for specific dates and questions\nCREATE TABLE user_answers (\n    answer_id INT PRIMARY KEY AUTO_INCREMENT,\n    user_id INT NOT NULL,\n    date_question_id INT,\n    answer_text TEXT NOT NULL,\n    FOREIGN KEY (date_question_id) REFERENCES date_questions(date_question_id)\n);