IF EXISTS (SELECT name FROM sys.columns \n             WHERE name = 'COLUMN_THAT_NO_LONGER_EXISTS'\n               AND object_id = OBJECT_ID('TABLE1'))\nBEGIN\n   DECLARE @SQL NVARCHAR(MAX);\n   \n   SET @SQL = N'INSERT INTO TABLE1 (' +\n              N'COLUMN_THAT_NO_LONGER_EXISTS, COLUMN_B, COLUMN_C)' +\n              N'SELECT 1, 2, 3 FROM TABLE2;' +\n              N'ALTER TABLE TABLE1 DROP COLUMN COLUMN_THAT_NO_LONGER_EXISTS;';\n\n   EXEC sp_executesql @SQL;\nEND