DECLARE @fromDate DATETIME = '2009-06-12';\nDECLARE @firstEverPayment DATETIME = '2009-06-01';\n\n-- Calculate the number of fortnights to add\nDECLARE @fortnightsToAdd INT;\nSET @fortnightsToAdd = DATEDIFF(DAY, @firstEverPayment, @fromDate) / 14;\n\n-- Adjust for partial fortnight if necessary\nIF (DATEDIFF(DAY, @firstEverPayment, @fromDate) % 14 <> 0)\n    SET @fortnightsToAdd = @fortnightsToAdd + 1;\n\nDECLARE @nextPayment DATETIME;\nSET @nextPayment = DATEADD(DAY, @fortnightsToAdd * 14, @firstEverPayment);\n\nSELECT @nextPayment AS NextPaymentDate;