UPDATE FUNCTIONS\nSET Func_TaxRef = (\n    SELECT Min(TAX.Tax_Code)\n    FROM TAX\n    WHERE FUNCTIONS.Func_Pure <= TAX.Tax_ToPrice AND FUNCTIONS.Func_Year = TAX.Tax_Year\n) \nWHERE EXISTS (\n    SELECT 1 \n    FROM TAX\n    WHERE FUNCTIONS.Func_Pure <= TAX.Tax_ToPrice AND FUNCTIONS.Func_Year = TAX.Tax_Year\n);