-- Tables to track sources and persons\nSource(id PRIMARY KEY)\nPerson(id PRIMARY KEY)\n\n-- Table for attributes (e.g., birth_date, color, relationship_type)\nAttribute(attribute_id PRIMARY KEY, name VARCHAR(255))\n\n-- Claims table linking sources, persons, and attributes\nClaim(\n    claim_id PRIMARY KEY,\n    source_id INTEGER,\n    person_id INTEGER,\n    attribute_id INTEGER,\n    value TEXT,  -- Use a generic type to store various kinds of values\n    FOREIGN KEY(source_id) REFERENCES Source(id),\n    FOREIGN KEY(person_id) REFERENCES Person(id),\n    FOREIGN KEY(attribute_id) REFERENCES Attribute(attribute_id)\n)\n\n-- Users table\nUser(id PRIMARY KEY)\n\n-- Confidence table linking users and claims\nConfidence(\n    user_id INTEGER,\n    claim_id INTEGER,\n    confidence REAL,  -- Use real or decimal to store percentage of confidence\n    PRIMARY KEY(user_id, claim_id),\n    FOREIGN KEY(user_id) REFERENCES User(id),\n    FOREIGN KEY(claim_id) REFERENCES Claim(claim_id)\n)