DECLARE @DatabaseName NVARCHAR(100) = N'dbase';\nDECLARE @TriggerSQL NVARCHAR(MAX);\n\n-- Construct the full T-SQL command as a single batch without using GO\nSET @TriggerSQL = N'\nUSE [' + @DatabaseName + '];\nCREATE TRIGGER [blah]\nON YourSchema.YourTable -- Use fully qualified name for table\nFOR INSERT, UPDATE, DELETE\nAS\nBEGIN\n    -- Trigger body here, e.g., log changes or enforce some rules\nEND;\n\n';\n\n-- Execute the dynamic SQL command\nEXEC sp_executesql @TriggerSQL;