-- Create a new XML Schema Collection\nCREATE XML SCHEMA COLLECTION MyXmlSchema AS '\n<xs:schema xmlns:xs="http://www.w3.org/2001/XMLSchema">\n  <xs:element name="Root">\n    <xs:complexType>\n      <xs:sequence>\n        <xs:element name="DateValue" type="xs:date"/>\n        <xs:element name="IntValue" type="xs:int"/>\n        <xs:element name="DecimalValue" type="xs:decimal"/>\n      </xs:sequence>\n    </xs:complexType>\n  </xs:element>\n</xs:schema>';\n\n-- Create a table with an XML column that uses the schema\nCREATE TABLE MyTable (\n    ID INT PRIMARY KEY,\n    XmlData XML(MyXmlSchema)\n);