SELECT \n    node.nid AS nid,\n    node_data_field_factsheet.field_factsheet_fid AS file_id,\n    files.uri AS file_path,\n    node.type AS node_type,\n    node.vid AS node_vid\nFROM \n    node node\nLEFT JOIN \n    content_type_fund node_data_field_factsheet ON node.vid = node_data_field_factsheet.vid\nLEFT JOIN \n    files files ON node_data_field_factsheet.field_factsheet_fid = files.fid\nWHERE \n    node.type IN ('fund');