declare @msg xml\n\nset @msg='<root><date>2009-05-09T13:50:59.6361485+08:00</date></root>'\n\n-- Use datetimeoffset to handle the timezone information\nselect \n    @msg.value(N'(//root/date/text())[1]', 'datetimeoffset') as DateTimeOffsetValue,\n    -- Convert to datetime if needed (will ignore timezone offset)\n    convert(datetime, @msg.value(N'(//root/date/text())[1]', 'datetimeoffset')) as DateTimeValue