-- General table with common fields\nCREATE TABLE media (\n    id SERIAL PRIMARY KEY,\n    name VARCHAR(100),\n    description TEXT,\n    url VARCHAR(255)\n);\n\n-- Specialized TV series table\nCREATE TABLE tv_series (\n    media_id INT PRIMARY KEY REFERENCES media(id) ON DELETE CASCADE,\n    season SMALLINT,\n    episode SMALLINT,\n    airing DATE\n);\n\n-- Specialized movies table\nCREATE TABLE movies (\n    media_id INT PRIMARY KEY REFERENCES media(id) ON DELETE CASCADE,\n    release_date DATE,\n    budget NUMERIC(10, 2)\n);