-- Departments with ManagerID as Nullable Foreign Key\nCREATE TABLE Departments (\n    DepartmentID INT PRIMARY KEY,\n    ManagerID INT,\n    FOREIGN KEY (ManagerID) REFERENCES Employees(EmployeeID)\n);\n\n-- Insert departments\nINSERT INTO Departments (DepartmentID, ManagerID)\nVALUES (1, NULL), (2, NULL);\n\n-- Insert employees specifying their department\nINSERT INTO Employees (EmployeeID, DepartmentID)\nVALUES (101, 1), (102, 2);\n\n-- Update部门 with manager IDs\nUPDATE Departments SET ManagerID = 101 WHERE DepartmentID = 1;\nUPDATE Departments SET ManagerID = 102 WHERE DepartmentID = 2;