-- Allow DepartmentID in Employees to temporarily accept NULLs during insertion\nALTER TABLE Employees ALTER COLUMN DepartmentID INT NULL;\n\n-- Insert initial department record with null EmployeeID\nINSERT INTO Departments (DepartmentID, EmployeeID)\nVALUES (1, NULL);\n\n-- Insert employee record into Employees\nINSERT INTO Employees (EmployeeID, DepartmentID)\nVALUES (101, 1);\n\n-- Update the Department with the newly added manager's ID\nUPDATE Departments SET EmployeeID = 101 WHERE DepartmentID = 1;\n\n-- Reset column constraint to NOT NULL if needed\nALTER TABLE Employees ALTER COLUMN DepartmentID INT NOT NULL;