SELECT \n    t.name AS TableName,\n    ep.value AS TableDescription,\n    c.name AS ColumnName,\n    ty.name AS DataType,\n    c.max_length AS MaxLength -- useful for types like varchar, nvarchar, etc.\nFROM \n    sys.tables t\nLEFT JOIN \n    sys.extended_properties ep ON t.object_id = ep.major_id\nLEFT JOIN \n    sys.columns c ON t.object_id = c.object_id\nJOIN \n    sys.types ty ON c.system_type_id = ty.system_type_id\n-- This filters the extended property to get only table descriptions.\nWHERE \n    ep.minor_id = 0 \nORDER BY \n    TableName, ColumnName;