-- Stores search sessions with timestamps to purge old entries.\nCREATE TABLE searches (\n  id INT AUTO_INCREMENT PRIMARY KEY,\n  user_id INT, -- Optional, if you want to associate searches with users.\n  creation_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n);\n\n-- Stores the result order of a particular search session.\nCREATE TABLE searches_results (\n  search_id INT,\n  result_id INT, -- User or content ID displayed as a result.\n  `order` INT, -- The random order assigned within this search.\n  FOREIGN KEY (search_id) REFERENCES searches(id)\n);