CREATE TRIGGER trg_BeforeDeleteCheck\nON YourTableName\nFOR DELETE\nAS\nBEGIN\n    -- Check if there are any rows that should not be deleted based on certain conditions\n    IF EXISTS (\n        SELECT 1 \n        FROM deleted d \n        WHERE d.SomeColumn = 'SomeValue' -- Replace with your condition\n    )\n    BEGIN\n        ROLLBACK TRANSACTION;\n        RAISERROR ('Cannot delete these records because they meet certain criteria.', 16, 1);\n        RETURN;\n    END;\n\n    -- Other pre-delete operations can be done here\nEND;