DECLARE @OLD_NAV_VALUE AS INT\nDECLARE @FINAL AS INT\nDECLARE @DYNAMIC_COL_NAME NVARCHAR(256) = N'col1' -- or whatever column name you are using dynamically\nDECLARE @id_Date DATE = '2023-10-01' -- set your date value\n\n-- Use sp_executesql to pass the variable in and out of the dynamic SQL\nEXEC sp_executesql \n    N'SELECT @OLD_NAV_VALUE_OUT = [' + @DYNAMIC_COL_NAME + '] FROM TBL_BA WHERE DATE = @id_Date_IN',\n    N'@OLD_NAV_VALUE_OUT INT OUTPUT, @id_Date_IN DATE',\n    @OLD_NAV_VALUE_OUTPUT = @OLD_NAV_VALUE OUTPUT,\n    @id_Date_IN = @id_Date\n\nSET @FINAL =  @OLD_NAV_VALUE * 50\n\n-- Output the results (optional)\nSELECT @OLD_NAV_VALUE AS OldNavValue, @FINAL AS FinalValue