-- Configure MySQL users in ProxySQL\nINSERT INTO mysql_users(username) VALUES('user');\n\n-- Add your shard backends\nINSERT INTO mysql_servers(hostgroup_id, hostname, port) VALUES (1, 'mysql_shard_1_host', 3306);\nINSERT INTO mysql_servers(hostgroup_id, hostname, port) VALUES (2, 'mysql_shard_2_host', 3306);\n\n-- Route queries based on table name\nINSERT INTO mysql_query_rules(active, match_digest, destination_hostgroup)\nVALUES(1, '^SELECT .* FROM table_1', 1),\n      (1, '^SELECT .* FROM table_2', 2);\n\n-- Load the rules into runtime and save to disk\nLOAD MYSQL QUERY RULES TO RUNTIME;\nSAVE MYSQL QUERY RULES TO DISK;\n\n-- Set read-only on hostgroups that should be treated as such\nUPDATE mysql_servers SET galera_writer_hostgroup=-1 WHERE hostgroup_id IN (1, 2);\nSAVE MYSQL SERVERS TO RUNTIME;\nSAVE MYSQL SERVERS TO DISK;