-- First, add the column without the NOT NULL constraint and set a default value\nALTER TABLE mytable ADD COLUMN mycolumn character varying(50) DEFAULT 'default_value';\n\n-- Update the column if you have specific values to insert\n-- UPDATE mytable SET mycolumn = 'specific_value' WHERE condition;\n\n-- Then, alter the column to add the NOT NULL constraint\nALTER TABLE mytable ALTER COLUMN mycolumn SET NOT NULL;