CREATE TABLE events (\n    event_id INT PRIMARY KEY AUTO_INCREMENT,\n    name VARCHAR(255),\n    start_date DATE\n);\n\nCREATE TABLE events_dateinfo (\n    dateinfo_id INT PRIMARY KEY AUTO_INCREMENT,\n    event_id INT,\n    repeat_type ENUM('day', 'week', 'month'),\n    interval_val INT, -- for every n days/weeks/months\n    day_of_week ENUM('Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'), -- for weekly repeats\n    day_of_month TINYINT, -- for monthly repeats\n    FOREIGN KEY (event_id) REFERENCES events(event_id)\n);