-- Adding Linked Server for Server1\nEXEC sp_addlinkedserver \n    @server='Server1', \n    @srvproduct='',\n    @provider='SQLOLEDB', \n    @datasrc='Server1_Name'; \n\nEXEC sp_addlinkedsrvlogin \n    @rmtsrvname = 'Server1',\n    @useself = 'False',\n    @locallogin = NULL,\n    @rmtuser = 'UsernameOnServer1',\n    @rmtpassword = 'PasswordOnServer1';\n\n-- Adding Linked Server for Server2\nEXEC sp_addlinkedserver \n    @server='Server2', \n    @srvproduct='',\n    @provider='SQLOLEDB', \n    @datasrc='Server2_Name'; \n\nEXEC sp_addlinkedsrvlogin \n    @rmtsrvname = 'Server2',\n    @useself = 'False',\n    @locallogin = NULL,\n    @rmtuser = 'UsernameOnServer2',\n    @rmtpassword = 'PasswordOnServer2';