DECLARE @idoc INT;\nDECLARE @xmlData VARCHAR(MAX);\n\n-- Step 1: Load large XML file as text (ensure the path to the file is correct)\nSELECT @xmlData = BulkColumn \nFROM OPENROWSET(BULK 'C:\path\to\your\file.xml', SINGLE_CLOB) AS xmlData;\n\n-- Step 2: Prepare XML document for reading\nEXEC sp_xml_preparedocument @idoc OUTPUT, @xmlData;\n\n-- Step 3: Insert data using OPENXML (customize the XPath and column definitions based on your XML structure)\nINSERT INTO YourTargetTable (Column1, Column2)\nSELECT Column1, Column2\nFROM OPENXML(@idoc, '/Root/ChildElement', 2) \nWITH (\n    Column1 VARCHAR(50),\n    Column2 VARCHAR(50)\n);\n\n-- Step 4: Release resources\nEXEC sp_xml_removedocument @idoc;