CREATE TABLE Scripts (\n    ScriptID INT PRIMARY KEY AUTO_INCREMENT,\n    ScriptName VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE Subscripts (\n    SubscriptID INT PRIMARY KEY AUTO_INCREMENT,\n    SubscriptName VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE Records (\n    RecordID INT PRIMARY KEY AUTO_INCREMENT,\n    RecordContent TEXT NOT NULL\n);\n\nCREATE TABLE Script_Subscript_Map (\n    MapID INT PRIMARY KEY AUTO_INCREMENT,\n    ScriptID INT,\n    SubscriptID INT,\n    FOREIGN KEY (ScriptID) REFERENCES Scripts(ScriptID),\n    FOREIGN KEY (SubscriptID) REFERENCES Subscripts(SubscriptID)\n);\n\nCREATE TABLE Subscript_Record_Map (\n    MapID INT PRIMARY KEY AUTO_INCREMENT,\n    SubscriptID INT,\n    RecordID INT,\n    FOREIGN KEY (SubscriptID) REFERENCES Subscripts(SubscriptID),\n    FOREIGN KEY (RecordID) REFERENCES Records(RecordID)\n);