DECLARE @SearchTerm NVARCHAR(MAX) = 'Overdose of "Vitamin C" for child*';\nDECLARE @SQL NVARCHAR(MAX);\n\n-- Split the search term into different parts\n-- This is a simplified example and might need more robust parsing logic\nSET @SQL = N'SELECT * FROM YourTable WHERE CONTAINS(YourColumn, ';\nSET @SQL += N'(''Overdose'') AND ';\nSET @SQL += N'("''Vitamin C''") AND '; -- Note the double single quotes in phrase search\nSET @SQL += N'(FORMSOF(INFLECTIONAL, child))';\nSET @SQL += N');';\n\n-- Execute the dynamic SQL\nEXEC sp_executesql @SQL;