-- Adjusting memory allocation in spfile\nALTER SYSTEM SET sga_target=2G SCOPE=SPFILE;\nALTER SYSTEM SET pga_aggregate_target=1G SCOPE=SPFILE;\n\n-- Disabling Oracle Text feature\nALTER SYSTEM SET "_oracle_text_enabled" = FALSE SCOPE=BOTH;\n\n-- Reducing number of processes\nALTER SYSTEM SET processes=50 SCOPE=SPFILE;\n\n-- Restart the database instance for changes to take effect\nSHUTDOWN IMMEDIATE;\nSTARTUP;