-- Create a new temporary table with the additional character column\nCREATE TEMP TABLE temptable_new (\n    numeric_column INT,\n    char_column CHAR(2)\n);\n\n-- Insert data into the new temporary table, applying the transformation logic\nINSERT INTO temptable_new (numeric_column, char_column)\nSELECT \n    numeric_column,\n    CASE \n        WHEN numeric_column <= 1 THEN 'SP'\n        WHEN numeric_column > 1 THEN 'FA'\n    END AS char_column\nFROM \n    temptable;\n\n-- If needed, drop the old temp table and rename the new one if context allows\nDROP TABLE temptable;\nCREATE TEMP TABLE temptable (\n    numeric_column INT,\n    char_column CHAR(2)\n);\n\nINSERT INTO temptable (numeric_column, char_column)\nSELECT * FROM temptable_new;\n\nDROP TABLE temptable_new;