BEGIN TRY\n    -- Assuming AddCustomer is written to handle multiple rows at once.\n    INSERT INTO SomeResultTable (CustomerID, CustomerName)\n    EXEC AddCustomer @CustomerID = NULL, @CustomerName = NULL; \n\n    -- If above doesn't work and you have control over the stored procedure,\n    -- modify it to handle table-valued parameters or a temporary table\n    DECLARE @CustomerTable TABLE (\n        CustomerID int,\n        CustomerName varchar(100)\n    );\n\n    INSERT INTO @CustomerTable (CustomerID, CustomerName)\n    SELECT CustomerID, CustomerName FROM Customers;\n\n    INSERT INTO SomeResultTable (CustomerID, CustomerName)\n    EXEC AddCustomer @Table = @CustomerTable;\nEND TRY\nBEGIN CATCH\n    PRINT 'An error occurred: ' + ERROR_MESSAGE();\nEND CATCH