-- Set a reasonable batch size based on your system's capacity.\nDECLARE @BatchSize INT = 1000;\nDECLARE @Offset INT = 0;\n\nWHILE (1=1)\nBEGIN\n    UPDATE TOP (@BatchSize) YourTable\n    SET YourNTextColumn = CONVERT(NVARCHAR(MAX), YourNTextColumn)\n    FROM YourTable WITH (INDEX(YourIndexName)) -- Optionally specify an index if needed for performance\n    OFFSET @Offset ROWS FETCH NEXT @BatchSize ROWS ONLY;\n\n    -- Check if any rows were updated, if not break the loop.\n    IF @@ROWCOUNT < @BatchSize BREAK;\n    \n    SET @Offset = @Offset + @BatchSize;\n\n    -- Optional: Add a wait time to reduce impact on server load\n    WAITFOR DELAY '00:00:01';\nEND;