WHILE (@count >= @daysBack)\nBEGIN\n    SET @date = DATEADD(dd, @count, GETDATE())\n\n    -- Create an intermediate temp table or table variable to hold the output of the stored procedure\n    DECLARE @intermediate TABLE (\n        Name VARCHAR(150),\n        Address VARCHAR(25)\n    )\n\n    -- Insert the results of the stored procedure into the intermediate table\n    INSERT INTO @intermediate (Name, Address)\n    EXEC [dbo].StoredProc @date\n\n    -- Insert from the intermediate table into your final temp table with the date\n    INSERT INTO @temp (Name, Address, Date)\n    SELECT Name, Address, @date\n    FROM @intermediate\n\n    SET @count = @count - 1\nEND