-- Step 1: Create a collection type for returning IDs\nCREATE OR REPLACE TYPE ID_LIST AS TABLE OF NUMBER;\n/\n\n-- Step 2: Create a pipelined function that returns the collection type\nCREATE OR REPLACE FUNCTION GET_OFFICE_IDS RETURN ID_LIST PIPELINED IS\nBEGIN\n    -- Example implementation, replace with your logic to fetch office IDs\n    FOR rec IN (SELECT OFFICE_ID FROM OFFICES WHERE SOME_CONDITION) LOOP\n        PIPE ROW(rec.OFFICE_ID);\n    END LOOP;\n    \n    -- Alternatively, insert static values for demonstration:\n    -- PIPE ROW(1);\n    -- PIPE ROW(2);\n    -- PIPE ROW(3);\n\n    RETURN;\nEND GET_OFFICE_IDS;\n/\n\n-- Using the pipelined function in a query\nSELECT *\nFROM EMPLOYEES\nWHERE OFFICE_ID IN (SELECT * FROM TABLE(GET_OFFICE_IDS));