CREATE FUNCTION dbo.INET_ATON(@ipAddress VARCHAR(15))\nRETURNS INT\nAS\nBEGIN\n    DECLARE @part INT, @result BIGINT = 0;\n    DECLARE @i INT = 1;\n\n    WHILE CHARINDEX('.', @ipAddress) > 0\n    BEGIN\n        SET @part = CAST(PARSENAME(REPLACE(@ipAddress, '.', '.'), @i) AS INT);\n        SET @result = @result + (@part * POWER(256, @i - 1));\n        SET @i = @i + 1;\n    END\n\n    SET @part = CAST(PARSENAME(REPLACE(@ipAddress, '.', '.'), @i) AS INT);\n    SET @result = @result + (@part * POWER(256, @i - 1));\n\n    RETURN CAST(@result AS INT);\nEND