DECLARE @ProcedureName NVARCHAR(128) = 'YourStoredProcedureName';\n\nSELECT \n    DB_NAME(database_id) AS DatabaseName,\n    OBJECT_NAME(object_id, database_id) AS ProcedureName,\n    type_desc \nFROM \n    sys.system_catalog_views scv\nCROSS APPLY \n    sys.dm_exec_function_stats(NULL, NULL, object_id(QUOTENAME(s.[name]) + '.' + QUOTENAME(p.[name]), 'P'), NULL)\nJOIN \n    sys.procedures p ON scv.object_id = p.object_id\nJOIN \n    sys.schemas s ON p.schema_id = s.schema_id\nWHERE \n    p.name LIKE '%' + @ProcedureName + '%'\nORDER BY \n    DatabaseName, ProcedureName;