DECLARE @TableName NVARCHAR(255)\nDECLARE @SQLQuery NVARCHAR(MAX)\n\n-- Cursor to iterate over all tables in the linked server whose name starts with 'text'\nDECLARE TableCursor CURSOR FOR \nSELECT TABLE_NAME \nFROM OPENQUERY(LinkedServer, 'SELECT table_name FROM information_schema.tables WHERE table_type = ''BASE TABLE'' AND table_name LIKE ''text%''')\n\nOPEN TableCursor\nFETCH NEXT FROM TableCursor INTO @TableName\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    -- Construct the SQL query to drop the local table if it exists\n    SET @SQLQuery = 'IF OBJECT_ID(''dbo.[' + @TableName + ']'') IS NOT NULL DROP TABLE dbo.[' + @TableName + ']'\n    EXEC sp_executesql @SQLQuery\n    \n    PRINT 'Dropped local table [' + @TableName + ']'\n\n    -- Construct the SQL query to select * into a new local table\n    SET @SQLQuery = 'SELECT * INTO dbo.[' + @TableName + '] FROM LinkedServer.DB.dbo.[' + @TableName + ']'\n    \n    BEGIN TRY\n        EXEC sp_executesql @SQLQuery\n        PRINT 'Copied data from [' + @TableName + '] to local table successfully.'\n    END TRY\n    BEGIN CATCH\n        -- Handle errors if any occur during the copy operation\n        PRINT 'Error copying data from [' + @TableName + '] to local table: ' + ERROR_MESSAGE()\n    END CATCH\n\n    FETCH NEXT FROM TableCursor INTO @TableName\nEND\n\nCLOSE TableCursor\nDEALLOCATE TableCursor